exports.buildPhotoQuery = function(apiKey, env, photoUrl) {
    var postURL = "http://" + (env=="stg"?"stg.":"") + "api.bazaarvoice.com/data/uploadphoto.json?ApiVersion=5.4&ContentType=review&PassKey="+apiKey+"&photoUrl="+photoUrl;
	return postURL;
};

exports.submitPhoto = function(queryString, callback){
	var request = require('request');

	request.post({
		headers: {'content-type' : 'application/x-www-form-urlencoded'},
		url: queryString,
	}, function(error, response, body){
		callback(JSON.parse(body));
	});
};